package loopWhile;

public class LoopWhile {
	/** Autor: LUCAS GOMES 
	 *  Data: 22/03/2022 **/

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=0;
		while(i<5) { //enquanto i for menor que 5 ele continua e acrescenta 1
			System.out.println(i);
			i++; //ingrementa 1
		}
	}

}
